import React, { useState } from "react";

import PostDetail from "../../pages/PostDetail";

/* 
工作日誌：
處理 modifiedDate 沒有寫入問題
處理 title content 讀不到問題
*/

const AddForm = ({ addPostHandler }) => {
  const userInfo = JSON.parse(localStorage.getItem("userInfo"));
  //console.log(userInfo);

  const [postObj, setPostObj] = useState({
    username: userInfo.name,
    email: userInfo.email,
    title: "",
    content: "",
    modifieddate: "",
    picture: userInfo.picture,
  });

  const formSubmitHandler = (e, addPostHandler, postObj) => {
    e.preventDefault();

    setModifiedDate(postObj);

    if (!postObj.title == "" && !postObj.content == "") {
      addPostHandler(postObj);
    } else {
      window.alert("Title and content cannot be empty!");
    }
  };

  const setModifiedDate = (prePostObj) => {
    const dateInfo = new Date();
    const modifiedDate = `${dateInfo.getFullYear()}-${dateInfo.getMonth()}-${dateInfo.getDate()}`;
    const newPostObj = {
      ...prePostObj,
      modifieddate: modifiedDate,
    };

    setPostObj(newPostObj);
  };

  const titleOnchangeHandler = (e, prePostObj) => {
    const newPostObj = {
      ...prePostObj,
      title: e.target.value,
    };
    return setPostObj(newPostObj);
  };

  const contentOnchangeHandler = (e, prePostObj) => {
    const newPostObj = {
      ...prePostObj,
      content: e.target.value,
    };
    setPostObj(newPostObj);
  };

  const testHandler = (e) => {
    e.preventDefault();
    console.log(postObj);
  };

  return (
    <React.Fragment>
      <div>
        <form>
          <label>Title:</label>
          <input
            type="text"
            value={postObj["title"]}
            onChange={(e) => {
              titleOnchangeHandler(e, postObj);
            }}
          ></input>
          <label>Content:</label>
          <textarea
            value={postObj["content"]}
            onChange={(e) => {
              contentOnchangeHandler(e, postObj);
            }}
          ></textarea>
          <button
            onClick={(e) => {
              formSubmitHandler(e, addPostHandler, postObj);
            }}
          >
            Submit
          </button>
          <button onClick={testHandler}>TEST</button>
        </form>
      </div>
    </React.Fragment>
  );
};

export default AddForm;
