import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  darkTheme: localStorage.getItem("theme") !== "true" ? true : false,
};

const ThemeSlice = createSlice({
  name: "theme",
  initialState,
  reducers: {
    toggleTheme(state) {
      state = !state;
    },
  },
});

export const LoginActions = ThemeSlice.actions;
export default ThemeSlice.reducer;
