import { initializeApp } from "firebase/app";
import { getFirestore } from "@firebase/firestore";

// firebase config object which is use for connecting
// apiKey shouldn't public!
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API,
  authDomain: "reactful-message-board-b474a.firebaseapp.com",
  projectId: "reactful-message-board-b474a",
  storageBucket: "reactful-message-board-b474a.appspot.com",
  messagingSenderId: "24183780399",
  appId: "1:24183780399:web:3b2af17743916a252482da",
  measurementId: "G-GFYLK096VC",
};

// connect firebase and app
const app = initializeApp(firebaseConfig);

// connect database
export const db = getFirestore(app);
