import React, { useEffect } from "react";

import PostList from "../components/post/PostList";
import useHttp from "../hook/useHttp";
import LoadingSpinner from "../components/UI/LoadingSpinner";
import { getAllPosts } from "../lib/firebase-api";

// const postItems = [
//   {
//     postId: "001",
//     modifiedDate: "2022/03/03",
//     userName: "Lucas",
//     title: "Nikki is so cute",
//     content: "Nikki is 100% cute",
//   },
//   {
//     postId: "002",
//     modifiedDate: "2022/09/06",
//     userName: "Zale",
//     title: "Learning React",
//     content: "wanna looking for a react job!",
//   },
//   {
//     postId: "003",
//     modifiedDate: "2022/07/30",
//     userName: "Alan",
//     title: "Let's go freediving!",
//     content: "Jsut do it!!!",
//   },
// ];

const AllPost = () => {
  const {
    sendRequest,
    status,
    data: allPosts,
    error,
  } = useHttp(getAllPosts, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="xxx">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className="centered focused">{error}</p>;
  }

  if (status === "completed" && (!allPosts || allPosts.length === 0)) {
    return <h1>NO DATA!!</h1>;
  }

  // console.log("AllPOST: " + allPosts);

  //console.log(getAllPosts());

  return (
    <React.Fragment>
      <PostList posts={allPosts} />
    </React.Fragment>
  );
};

export default AllPost;
