import React, { useState } from "react";
import PostDetail from "../../pages/PostDetail";

const AddForm = (props) => {
  const [postObj, setPostObj] = useState({
    username: "",
    email: "",
    title: "",
    content: "",
    modifieddate: "",
    picture: "",
  });

  const post = {};

  const userInfo = JSON.parse(localStorage.getItem("userInfo"));
  console.log(userInfo);

  const formSubmitHandler = (e, props) => {
    e.preventDefault();

    const dateInfo = new Date();
    const modifiedDate = `${dateInfo.getFullYear()}-${dateInfo.getMonth()}-${dateInfo.getDate()}`;
    console.log(modifiedDate);

    postObj.username = userInfo.name;
    postObj.email = userInfo.email;

    if (!postObj.title == "" && !postObj.content == "") {
      postObj.username = props.addPostHandler(postObj);
    } else {
      window.alert("Title and content cannot be empty!");
    }
  };

  return (
    <React.Fragment>
      <div>
        <form>
          <label>Title:</label>
          <input
            type="text"
            value={postObj.title}
            onChange={(e) => {
              setPostObj({ title: e.target.value });
            }}
          ></input>
          <label>Content:</label>
          <textarea
            value={postObj.content}
            onChange={(e) => {
              postObj.content = e.target.value;
            }}
          ></textarea>
          <button onClick={formSubmitHandler}>Submit</button>
        </form>
      </div>
    </React.Fragment>
  );
};

export default AddForm;
