import React, { useEffect } from "react";
import { NavLink, Link } from "react-router-dom";

import styles from "./Navigation.module.scss";
import logo from "../../images/logo.png";
import { useSelector, useDispatch } from "react-redux";
import { LoginActions } from "../../store/LoginSlice";

const Navigation = () => {
  const isLogin = useSelector((state) => state.isLogin);
  const dispatch = useDispatch();

  const loginHandler = () => {
    dispatch(LoginActions.userLogin());
    console.log("userLogin: " + isLogin);
  };

  // Google Login
  const handleCallbackResponse = (response) => {
    console.log("Encoded JWT ID token: " + response.credential);
  };

  useEffect(() => {
    /* global google */
    google.accounts.id.initialize({
      client_id:
        "714826054625-knlhrtd804a74g79anl025thrqgne5hs.apps.googleusercontent.com",
      callback: handleCallbackResponse,
    });
    google.accounts.id.renderButton(document.getElementById("loginBtn"), {
      theme: "inline",
      size: "large",
    });
  }, []);

  return (
    <header className={styles.header}>
      <div className={styles.logo}>
        <Link to="/allpost">
          <img src={logo} alt="ReactFul" />
        </Link>
      </div>
      <nav className={styles.nav}>
        <ul>
          <li>
            <NavLink
              to="/allpost"
              className={(navData) =>
                navData.isActive ? styles.active : styles.link
              }
            >
              All Post
            </NavLink>
          </li>
          {isLogin ? (
            <li>
              <NavLink
                to="/addpost"
                className={(navData) =>
                  navData.isActive ? styles.active : styles.link
                }
              >
                Add Post
              </NavLink>
            </li>
          ) : (
            ""
          )}
          {isLogin ? (
            <li>
              <NavLink
                to="/mypost"
                className={(navData) =>
                  navData.isActive ? styles.active : styles.link
                }
              >
                My Post
              </NavLink>
            </li>
          ) : (
            ""
          )}
          <button onClick={loginHandler}>{isLogin ? "Logout" : "Login"}</button>
          <button>Theme</button>
          <div id="loginBtn"></div>
        </ul>
      </nav>
    </header>
  );
};

export default Navigation;
