import React from "react";

import styles from "./AddForm.module.scss";

const getModifiedDate = (dateInfo) => {
  return `${dateInfo.getFullYear()}/${dateInfo.getMonth()}/${dateInfo.getDate()}`;
};

const titleOnchangeHandler = (e, prePostObj) => {
  const modifiedDate = getModifiedDate(new Date());
  const newPostObj = {
    ...prePostObj,
    title: e.target.value,
    modifieddate: modifiedDate,
  };
  setPostObj(newPostObj);
};

const contentOnchangeHandler = (e, prePostObj) => {
  const modifiedDate = getModifiedDate(new Date());
  const newPostObj = {
    ...prePostObj,
    content: e.target.value,
    modifieddate: modifiedDate,
  };
  setPostObj(newPostObj);
};

const testHandler = (e) => {
  e.preventDefault();
  console.log(postObj);
};

const PostForm = ({
  addPostHandler,
  formSubmitHandler,
  postObj,
  setPostObj,
}) => {
  return (
    <React.Fragment>
      <div className={styles.formContainer}>
        <form>
          <h2>New Post</h2>
          <input
            type="text"
            placeholder="Enter Title..."
            value={postObj["title"]}
            onChange={(e) => {
              titleOnchangeHandler(e, postObj);
            }}
          ></input>

          <textarea
            value={postObj["content"]}
            placeholder="Content..."
            onChange={(e) => {
              contentOnchangeHandler(e, postObj);
            }}
          ></textarea>
          <button
            onClick={(e) => {
              formSubmitHandler(e, addPostHandler, postObj);
            }}
          >
            Submit
          </button>
        </form>
      </div>
    </React.Fragment>
  );
};

export default PostForm;
