import React, { useEffect } from "react";
import { NavLink, Link } from "react-router-dom";

import styles from "./Navigation.module.scss";
import logo from "../../images/logo.png";
import { useSelector, useDispatch } from "react-redux";
import { GoogleLogin } from "../../lib/googleLogin";
import { LoginActions } from "../../store/LoginSlice";

const Navigation = () => {
  const isLogin = useSelector((state) => state.isLogin);
  const dispatch = useDispatch();

  const loginHandler = () => {
    dispatch(LoginActions.userLogin());
  };

  const logoutHandler = () => {
    dispatch(LoginActions.userLogout());
  };

  const getUserInfo = () => {
    return localStorage.getItem("userInfo");
  };
  var userInfo = getUserInfo();
  useEffect(() => {
    console.log(isLogin);
    console.log(userInfo);
    if (userInfo !== null) {
      loginHandler();
      console.log(isLogin);
    } else if (!isLogin) {
      GoogleLogin();
    }
  }, [isLogin, userInfo]);

  const getLog = () => {
    console.log("isLogin: " + isLogin);
    console.log("userInfo: " + userInfo);
  };

  return (
    <header className={styles.header}>
      <div className={styles.logo}>
        <Link to="/allpost">
          <img src={logo} alt="ReactFul" />
        </Link>
      </div>
      <nav className={styles.nav}>
        <ul>
          <li>
            <NavLink
              to="/allpost"
              className={(navData) =>
                navData.isActive ? styles.active : styles.link
              }
            >
              All Post
            </NavLink>
          </li>
          {isLogin ? (
            <li>
              <NavLink
                to="/addpost"
                className={(navData) =>
                  navData.isActive ? styles.active : styles.link
                }
              >
                Add Post
              </NavLink>
            </li>
          ) : (
            ""
          )}
          {isLogin ? (
            <li>
              <NavLink
                to="/mypost"
                className={(navData) =>
                  navData.isActive ? styles.active : styles.link
                }
              >
                My Post
              </NavLink>
            </li>
          ) : (
            ""
          )}
          {isLogin ? (
            <button onClick={logoutHandler}>Logout</button>
          ) : (
            <div onClick={loginHandler} id="loginBtn"></div>
          )}
          <button>Theme</button>
          <button onClick={getLog}>TEST</button>
        </ul>
      </nav>
    </header>
  );
};

export default Navigation;
