import React, { useState } from "react";

const AddForm = () => {
  const [postObj, setPostObj] = useState({
    username: null,
    email: null,
    title: null,
    content: null,
    modifieddate: null,
  });

  const formSubmitHandler = (e) => {
    e.preventDefault();
    const dateInfo = new Date();
    const modifiedDate = `${dateInfo.getFullYear()}-${dateInfo.getMonth()}-${dateInfo.getDate()}`;
    console.log(modifiedDate);

    const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    //console.log(userInfo);
  };

  return (
    <React.Fragment>
      <div>
        <from>
          <label>Title:</label>
          <input
            type="text"
            value={postObj.title}
            onChange={setPostObj()}
          ></input>
          <label>Content:</label>
          <textarea></textarea>
          <button onClick={formSubmitHandler}>Submit</button>
        </from>
      </div>
    </React.Fragment>
  );
};

export default AddForm;
