import React from "react";
import { NavLink } from "react-router-dom";

import styles from "./Navigation.module.scss";

const Navigation = () => {
  return (
    <header className={styles.header}>
      <div className={styles.logo}>LOGO</div>
      <nav className={styles.nav}>
        <ul>
          <li>
            <NavLink
              to="/allpost"
              className={(navData) => (navData ? styles.link : styles.active)}
            >
              All Post
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/addpost"
              className={(navData) => (navData ? "styles.link" : "")}
            >
              Add Post
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/mypost"
              className={(navData) => (navData ? "styles.link" : "")}
            >
              My Post
            </NavLink>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Navigation;
