// const initialTheme = getTheme();
// console.log("initialTheme: " + initialTheme);

// const initialState = {
//   darkTheme: false,
// };

const ThemeSlice = createSlice({
  name: "theme",
  initialState: { darkTheme: false },
  reducers: {
    toggleTheme(state) {
      state = !state;
    },
  },
});

export const ThemeAction = ThemeSlice.actions;
export default ThemeSlice.reducer;
