import { configureStore } from "@reduxjs/toolkit";

import LoginSlice from "./LoginSlice";
import ThemeSlice from "./ThemeSlice";

const reducers = { LoginSlice, ThemeSlice };

const store = configureStore({
  reducer: reducers,
});

export default store;
