import { createSlice } from "@reduxjs/toolkit";

const initialState = { isLogin: false };

const LoginSlice = createSlice({
  name: "login",
  initialState,
  reducers: {
    userLogin(state) {
      if (state.isLogin == false) {
        state.isLogin = true;
      } else if (state.isLogin == true) {
        state.isLogin = false;
      }
    },
  },
});

export default LoginSlice.reducer;
