import React from "react";
import { Link, useLocation } from "react-router-dom";

import styles from "./PostItem.module.scss";
import { deletePost } from "../../lib/firebase-api";

const PostItem = (props) => {
  const location = useLocation();
  const pathname = location.pathname;
  console.log(pathname);

  const deletePostHandler = (postid) => {
    try {
      const deletePost = window.confirm("Are you sure to delete post?");
      if (deletePost) {
        deletePost(postid);
        window.alert("Post deleted!");
        window.location.reload();
      }
    } catch {
      window.alert("Something went worng... Please try again later.");
    }
  };

  return (
    <li className={styles.items}>
      <figure>
        <blockquote>
          <h3>{props.title}</h3>
          <p>{props.content}</p>
        </blockquote>
        <div className={styles.infoContainer}>
          <figcaption>{props.userName}</figcaption>
          <span> modified at {props.modifiedDate}</span>
        </div>
      </figure>
      <div className={styles.linkBtn}>
        <Link
          className={styles.primary}
          to={`${location.pathname}/${props.postId}`}
        >
          {props.btnText}
        </Link>
        {pathname == "/mypost" ? (
          <Link
            className={styles.delete}
            to={"#"}
            onClick={() => {
              deletePostHandler(props.postId);
            }}
          >
            Delete
          </Link>
        ) : (
          ""
        )}
      </div>
    </li>
  );
};

export default PostItem;
