import React, { useEffect, useState, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";

import useHttp from "../hook/useHttp";
import { getSinglePost } from "../lib/firebase-api";
import PleaseLogin from "../components/UI/PleaseLogin";
import EditForm from "../components/post/EditForm";

const EditPost = () => {
  const isLogin = useSelector((state) => state.isLogin);

  const { sendEditRequest, editStatus } = useHttp();
  const params = useParams();
  const { postid } = params;
  const navigate = useNavigate();

  const responseData = async () => {
    const data = await getSinglePost(postid).then((data) => {
      return data;
      //console.log(data);
    });
  };

  const resolveData = async () => {
    // return await responseData;
  };

  useEffect(() => {
    if (editStatus === "completed") {
      window.alert("Post Edited!");
      navigate("/mypost", { replace: true });
    }
  });

  const editPostHandler = (postObj) => {
    sendEditRequest(postObj);
  };

  return (
    <React.Fragment>
      {isLogin ? (
        <EditForm addPostHandler={editPostHandler} />
      ) : (
        <PleaseLogin pageName={"Edit Post"} />
      )}
    </React.Fragment>
  );
};

export default EditPost;
