import jwt_decode from "jwt-decode";

export const GoogleLogin = () => {
  const handleCallbackResponse = (response) => {
    // console.log("Encoded JWT ID token: " + response.credential);
    var userObject = jwt_decode(response.credential);
    console.log(userObject);

    localStorage.setItem("userInfo", userObject);
  };

  google.accounts.id.initialize({
    /* global google */
    client_id:
      "714826054625-knlhrtd804a74g79anl025thrqgne5hs.apps.googleusercontent.com",
    callback: handleCallbackResponse,
  });

  google.accounts.id.renderButton(document.getElementById("loginBtn"), {
    theme: "outline",
    size: "large",
  });
};

/*
登入流程邏輯：
isLogin state 預設為 false
useEffect 載入先確認 localStorge 內是否有使用者資料
如果有 isLogin = true
沒有的話顯示登入按鈕

使用者登入以後
把使用者資訊存入 localStorge
並把 isLogin = true

登出流程：
把 localStorge 內的使用者資料設定為 null
並把 isLogin 設定為 flase
*/
