import React from "react";

const PostForm = () => {
  return (
    <React.Fragment>
      <div className={styles.formContainer}>
        <form>
          <h2>New Post</h2>
          <input
            type="text"
            placeholder="Enter Title..."
            value={postObj["title"]}
            onChange={(e) => {
              titleOnchangeHandler(e, postObj);
            }}
          ></input>

          <textarea
            value={postObj["content"]}
            placeholder="Content..."
            onChange={(e) => {
              contentOnchangeHandler(e, postObj);
            }}
          ></textarea>
          <button
            onClick={(e) => {
              formSubmitHandler(e, addPostHandler, postObj);
            }}
          >
            Submit
          </button>
        </form>
      </div>
    </React.Fragment>
  );
};

export default PostForm;
