      window.alert("Title and content cannot be empty!");
    }
  };

  return (
    <React.Fragment>
      <PostForm
        editPostHandler={editPostHandler}
        formSubmitHandler={formSubmitHandler}
        postObj={postObj}
        setPostObj={setPostObj}
        pageTitle={"Edit Post"}
      />
    </React.Fragment>
  );
};

export default EditForm;
