import React from "react";
import Navigation from "./Navigation";

import styles from "./Layout.module.scss";
import Footer from "./Footer";

const Layout = (props) => {
  return (
    <React.Fragment>
      <Navigation />
      <main className={styles.main}>{props.children}</main>
      <Footer />
    </React.Fragment>
  );
};

export default Layout;
